--  criação da tabela categoria

CREATE TABLE public.categoria (
	id_categoria int8 GENERATED BY DEFAULT AS IDENTITY( INCREMENT BY 1 MINVALUE 1 MAXVALUE 9223372036854775807 START 1 CACHE 1 NO CYCLE) NOT NULL,
	nome_categoria varchar(255) NULL,
	area_conhecimento varchar(255) NULL,
	descricao varchar(255) NULL,
	CONSTRAINT categoria_pkey PRIMARY KEY (id_categoria),
	CONSTRAINT categoria_nome_categoria_key UNIQUE (nome_categoria)
);

